# Open Excel workbook
excel_file = 'Book2.xlsx'  
wb = openpyxl.load_workbook(excel_file)
sheet = wb.active


email_column = sheet['B']


smtp = smtplib.SMTP(smtp_server, smtp_port)
smtp.starttls()


smtp.login(sender_email, password)


for i, cell in enumerate(email_column):
    if skip_first_row:
        skip_first_row = False
        continue 

    recipient_email = str(cell.value)  

    if recipient_email:
      
        msg = MIMEMultipart()
        msg['From'] = sender_email
        msg['To'] = recipient_email
        msg['Subject'] = subject

        msg.attach(MIMEText(message, 'html')) 

        print(f"Sending email to: {recipient_email}")
        
        smtp.sendmail(sender_email, recipient_email, msg.as_string())

        if (i + 1) % batch_size == 0:
            print(f"Pausing for {pause_interval} seconds...")
            smtp.quit()
            time.sleep(pause_interval)
            smtp = smtplib.SMTP(smtp_server, smtp_port)
            smtp.starttls()
            smtp.login(sender_email, password)


smtp.quit()

print('Emails sent individually to each recipient successfully')

wb.close()